import axios from "@/libs/api.request";
// import edit from "./category-edit";
export default {
  name: "",
  components: {
    // edit
  },
  props: {
    type: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      loading: false,
      showEditor: false,
      form: {},
      column: [
        {
          title: "分类名称",
          key: "tag"
        },
        {
          title: "描述",
          key: "introduce"
        },
        {
          title: "操作",
          key: "action",
          align: "center",
          width: 200,
          render: (h, params) => {
            return h("div", [
              h(
                "a",
                {
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.handleEdit(params.row);
                    }
                  }
                },
                "编辑"
              ),
              h(
                "a",
                {
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.handleRemove(params.row);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      data: [],
      pagination: {},
      page: 1
    };
  },
  methods: {
    newUser() {
      this.showEditor = true;
      this.form = {  };
    },
    saved() {
      this.showEditor = false;
      this.getData();
    },
    getData() {
      axios
        .request({
          method: "get",
          url: "api/food/food",
          params: {
            page:this.page
          }
        })
        .then(res => {
          this.data = res.data.data;
          this.pagination = res.data.pagination;
        });
    },
    handleRemove(row) {
      this.$Modal.confirm({
        title: "确认删除",
        content: "<p>确认删除该条记录</p>",
        onOk: () => {
          axios
            .request({
              method: "delete",
              url: "api/cost/tags/" + row.id
            })
            .then(res => {
              this.getData();
            });
        },
        onCancel: () => {}
      });
    },
    handleEdit(data) {
      this.form = data;
      this.showEditor = true;
    },
    changePage(page) {
      this.page = page;
      this.getData();
    }
  },
  mounted() {
    this.getData();
  }
};
